client_script:
frappe.web_form.after_load = function() {
    setTimeout(function() {
        if (frappe.web_form.doc.email && frappe.web_form.doc.otp) {
            frappe.web_form.save()
        } 
    }, 500)
    frappe.msgprint("Your OTP is Valid only for 30 Minutes")
}


frappe.web_form.handle_success = () => {
    frappe.msgprint({
        title: __('Success'),
        message: __('Your OTP is correct. Please Proceed to registration'),
        primary_action: {
            label: 'Proceed',
            action: function() {
                window.location = frappe.web_form.success_url + '?new=1&email_address=' + frappe.web_form.doc.email + '&otp=' + frappe.web_form.doc.otp
            }
        }
    });
}
