client_script:
frappe.web_form.validate = () => {
        var email = frappe.web_form.get_values().email
        var email_pattern = "^[a-zA-Z0-9+_.-]+@[a-zA-Z0-9.-]+$"; 
         if(!email.match(email_pattern))
        {
            frappe.msgprint('Enter Valid Email.')
            return false;
        }
        else{
        return true;
        }


}

frappe.web_form.handle_success = () => {
    frappe.msgprint({
        title: __('Success'),
        message: __('Your OTP has been sent on your email!'),
        primary_action: {
            label: 'Proceed',
            action: function() {
                window.location = frappe.web_form.success_url + '?new=1&email=' + frappe.web_form.doc.email
            }
        }
    });

}
